INSERT INTO `glossaries` (`id`, `name`, `slug`, `description`, `created_at`, `updated_at`, `deleted_at`) VALUES
(1, 'Empiècement', 'empiecement', 'Pièce de tissu rapportée sur un vêtement et assemblée au reste de ce vêtement, afin de respecter le style de celui-ci.', '2023-05-11 13:12:58', '2023-05-11 13:12:58', NULL),
(2, 'Galon', 'galon', 'Bande de tissu  (soie, laine, or ou argent,…) cousu au bord ou sur les coutures de vêtements pour les empêcher de s’effilocher ou pour servir de décoration.', '2023-05-11 13:12:58', '2023-05-11 13:12:58', NULL),
(3, 'Sous-patte', 'sous-patte', '(boutonnage). Système de fermeture d\'un vêtement composé de boutonnières cachées sous une bande de tissu rapportée ou à-même.', '2023-05-11 13:12:58', '2023-05-11 13:12:58', NULL),
(4, 'Gousset', 'gousset', '(ou soufflet) Pièce de tissu insérée sous le bras, lors de la conception d\'un kimono. En forme de losange, d\'une façon clas-sique, cette pièce est généralement coupée dans le plein biais.', '2023-05-11 13:12:58', '2023-05-11 13:12:58', NULL),
(5, 'Point Bourdon', 'point-bourdon', 'Point de couture à réaliser avec du coton à broder un peu épais, qui se réalise de gauche à droite ou de droite à gauche.', '2023-05-11 13:12:58', '2023-05-11 13:12:58', NULL),
(6, 'Montant', 'montant', 'Représente aussi la partie montante d\'un col.', '2023-05-11 13:12:58', '2023-05-11 13:12:58', NULL),
(7, 'Bordé', 'borde', 'Bande DL ou plein biais posée à cheval sur le ou les bords rappel, d\'un morceau de vêtement.', '2023-05-11 13:12:58', '2023-05-11 13:12:58', NULL),
(8, 'Passepoil', 'passepoil', 'Il s’agit d’une « fine bande de tissu, placée entre 2 bordures de couture, qui dépasse légèrement pour réaliser de jolies finitions (sur les bords d’un coussin par exemple, ou encore pour mettre en avant les lignes d’un vêtement). Piquez le passepoil à la machine en utilisant un pied spécial (pied pour fermeture à glissière) qui vous permettra de coudre le plus près possible du cordon. » comme l’explique Louis Antoinette sur son blog de couture.', '2023-05-11 13:12:58', '2023-05-11 13:12:58', NULL),
(9, 'Montant (pantalon)', 'montant-pantalon', 'Hauteur définie sur le milieu devant, entre la taille et la base de la fourche (devant).', '2023-05-11 13:12:58', '2023-05-11 13:12:58', NULL),
(10, 'Tricot', 'tricot', 'Technique utilisée pour fabriquer une étoffer à partir d’un fil. Les boucles constitutives d’un tricot sont appelées mailles, qui sont tenues sur des aiguilles.', '2023-05-11 13:12:58', '2023-05-11 13:12:58', NULL),
(11, 'Endroit', 'endroit', 'L’endroit est la face du tissu qui est visible de l’extérieur, la face où les motifs sont imprimés et bien visibles. L’envers, c’est l’autre face, c’est-à-dire la face intérieure du tissu.', '2023-05-11 13:12:58', '2023-05-11 13:12:58', NULL),
(12, 'Galon', 'galon', 'Bande de tissu  (soie, laine, or ou argent,…) cousu au bord ou sur les coutures de vêtements pour les empêcher de s’effilocher ou pour servir de décoration.', '2023-05-11 13:12:58', '2023-05-11 13:12:58', NULL),
(13, 'DI', 'di', 'Exprime le droit fil trame, c\'est-à-dire le sens travers du tissu (largeur), en opposition au DL. Un vêtement dont le DI est placé sur le milieu devant est coupé perpendiculairement à la lisière du tissu.', '2023-05-11 13:12:58', '2023-05-11 13:12:58', NULL),
(14, 'Basque', 'basque', 'Pièce de tissu fixée au niveau de la taille et tombant plus ou moins sur les hanches.', '2023-05-11 13:12:58', '2023-05-11 13:12:58', NULL),
(15, 'Parmenture', 'parmenture', 'Pièce de tissu permettant de renforcer ou de réaliser une jolie finition au niveau de l’ouverture d’un vêtement.', '2023-05-11 13:12:58', '2023-05-11 13:12:58', NULL),
(16, 'Sens du tissus', 'sens-du-tissus', 'Connaître le sens du tissu est important pour déterminer la façon dont le tissu sera coupé et assemblé durant la couture, il tient compte du tissage du tissu, mais aussi des motifs de ce dernier. Pour définir le sens du tissu, il est important de connaître la lisière et le droit-fil.', '2023-05-11 13:12:58', '2023-05-11 13:12:58', NULL),
(17, 'Bord-côte', 'bord-cote', 'Bande tricotée à côtes (élastique), permettant la finition d\'une ouverture d\'un vêtement: ex : bas de manche, encolure de pull-over... Cette matière peut aussi avoir d\'autres utilisations sur un plan plus créatif. Le bord côte peut être à-même, c\'est-à-dire qu\'il fait partie du morceau principal tricoté. Le bord côte peut aussi être rapporté, c\'est-à-dire qu\'il est cousu au morceau principal.', '2023-05-11 13:12:58', '2023-05-11 13:12:58', NULL),
(18, 'Incruster', 'incruster', 'Insérer un morceau de patron dans un autre, plus grand, en évidant celui-ci.', '2023-05-11 13:12:58', '2023-05-11 13:12:58', NULL),
(19, 'Pliure', 'pliure', 'Action de rabattre une partie de tissu sur une autre.', '2023-05-11 13:12:58', '2023-05-11 13:12:58', NULL),
(20, 'Pince', 'pince', 'Volume en forme de V, cousu sur l\'envers, permettant un ajustement du vêtement sur le corps.', '2023-05-11 13:12:58', '2023-05-11 13:12:58', NULL),
(21, 'Pistolet', 'pistolet', 'Règle plastique de forme courbée, servant à dessiner chaque courbe d\'un tracé. Le plus utilisé étant le pistolet N°21.', '2023-05-11 13:12:58', '2023-05-11 13:12:58', NULL),
(22, 'Renversement de sens', 'renversement-de-sens', 'Déplacer une pince en pivotant celle-ci par sa base (pivot de pince).', '2023-05-11 13:12:58', '2023-05-11 13:12:58', NULL),
(23, 'Platitude', 'platitude', 'Zone plate définie sur un tracé par une valeur de quelques centimêtres, en général en perpendiculaire, de façon à garantir un montage parfait en symétrie. Exemple : l\'entournure de base de la veste tailleur est tracée sur le devant, avec une valeur de platitude définie (2 cm).', '2023-05-11 13:12:58', '2023-05-11 13:12:58', NULL),
(24, 'Dégarnir', 'degarnir', 'Dégarnir les angles consiste à couper le surplus de tissu sur un angle, cela élimine les excédents de tissus qui autrement, formeront des épaisseurs sur les angles lorsque le tissu cousu est retourné.', '2023-05-11 13:12:58', '2023-05-11 13:12:58', NULL),
(25, 'Epaulette', 'epaulette', 'Ouatine coupée en demi-cercle placé sur l\'extrémité d\'épaule au bord de l\'entournure, afin de mieux structurer le vêtement et rehausser la pente d\'épaule.', '2023-05-11 13:12:58', '2023-05-11 13:12:58', NULL),
(26, 'Régler', 'regler', 'Consiste à contrôler les proportions définies sur chaque morceau du patron, à améliorer les lignes de ces différents morceaux devant être assemblés ensemble. Le réglage peut se faire sur une toile, sur un tracé de coupe à plat...', '2023-05-11 13:12:58', '2023-05-11 13:12:58', NULL),
(27, 'Cran de montage', 'cran-de-montage', 'Permet de raccorder deux morceaux de tissu entre biais. eux, pour un meilleur respect du montage et des volumes souhaités. Le cran de montage est toujours tracé à la perpendiculaire de la couture.', '2023-05-11 13:12:58', '2023-05-11 13:12:58', NULL),
(28, 'Patron', 'patron', 'Empreinte d\'un vêtement réalisé sur papier, avec toutes les finitions nécessaires à sa réalisation (crans, valeurs de couture, etc.). Ce tracé sert à la découpe du vêtement dans le tissu.', '2023-05-11 13:12:58', '2023-05-11 13:12:58', NULL),
(29, 'Écart', 'ecart', 'Mesure existant entre deux points, par exemple l\'écart poitrine (valeur comprise entre les deux points de poitrine).', '2023-05-11 13:12:58', '2023-05-11 13:12:58', NULL),
(30, 'Gradation', 'gradation', 'Système permettant, à partir d\'un vêtement, d\'augmen ter ou de réduire son volume le plus fidèlement possible. Exemple : à partir d\'une taille 38, on peut soit obtenir une taille 36 ou 40,42...', '2023-05-11 13:12:58', '2023-05-11 13:12:58', NULL),
(31, 'Tombant', 'tombant', 'Visuel que donne le vêtement sur le corps, découlant d\'un bon aplomb, d\'une bonne coupe.', '2023-05-11 13:12:58', '2023-05-11 13:12:58', NULL),
(32, 'Relever', 'relever', 'Reproduire, sur une autre feuille de papier, l\'empreinte d\'un morceau, en respectant exactement les contours de celui-ci. La reproduction peut se faire : ⁃	par transparence : on positionne la nouvelle feuille, puis, en trans-parence, à l\'aide d\'un crayon, on relève le morceau ⁃	par piquage : on positionne la nouvelle feuille en dessous du mor-ceau, puis, à l\'aide d\'un poinçon, on relève le morceau en piquant sur les tracés de celui-ci.', '2023-05-11 13:12:58', '2023-05-11 13:12:58', NULL),
(33, 'Perpendiculaire', 'perpendiculaire', 'Se dit d\'une ligne positionnée à 9o° par rapport à une autre. Exemple : la ligne des grandes hanches est perpendiculaire au milieu devant.', '2023-05-11 13:12:58', '2023-05-11 13:12:58', NULL),
(34, 'Bride', 'bride', 'Petite boucle de fils tressés, ou de tissu coup dans le plein biais, fixée sur le bord d\'un vêtement, pour permettre le passage d\'un bouton.', '2023-05-11 13:12:58', '2023-05-11 13:12:58', NULL),
(35, 'Appliquer', 'appliquer', 'Poser un morceau de tissu sur un autre plus grand, en les servant à la fermeture d\'un vêtement. fixant lun sur l\'autre.', '2023-05-11 13:12:58', '2023-05-11 13:12:58', NULL),
(36, 'Creuser', 'creuser', 'Augmenter la profondeur d\'une pince ou d\'une couture. Retailler une encolure, une entournure.', '2023-05-11 13:12:58', '2023-05-11 13:12:58', NULL),
(37, 'Bâtir', 'batir', 'Assembler ses différents tissus de manière grossière en réalisant de gros point de couture à la main.', '2023-05-11 13:12:58', '2023-05-11 13:12:58', NULL),
(38, 'Surjet', 'surjet', 'Action d\'assembler deux pièces ensemble, à l\'aide d\'une (surjeteuse qui coupe d\'abord le trop plein de matières avant d\'assembler ces deux morceaux par un point élastique. Ce principe est utilisé soit, dans le travail de la maille, qui nécessite de l\'élasticité, soit en bordant les morceaux d\'un vêtement avant d\'assembler les morceaux, de façon à éviter l\'effilochage de ceux-ci.', '2023-05-11 13:12:58', '2023-05-11 13:12:58', NULL),
(39, 'Surpiqûre', 'surpiqure', 'Il s’agit d’une décoration qui intervient dans la finition d’un vêtement ou d’un pièce en cuir (pensez à la surpiqure rouge sur les jeans ou les sièges en cuir d’une voiture de sport). La couturière vient piquer avec un point décoratif ou un fil d’une autre couleur.', '2023-05-11 13:12:58', '2023-05-11 13:12:58', NULL),
(40, 'Galbe', 'galbe', 'Exprime une ligne en courbe. Galber une ligne consiste à la courber.', '2023-05-11 13:12:58', '2023-05-11 13:12:58', NULL),
(41, 'Bouton', 'bouton', 'Pièce réalisée en métal, bois, corne, nacre, plastique, etc...,', '2023-05-11 13:12:58', '2023-05-11 13:12:58', NULL),
(42, 'Contre-fil', 'contre-fil', 'Le contre fil est le sens des fils perpendiculaires à la lisière (et donc à un angle de 90° par rapport au droit-fil). Coupé suivant le sens contre fil, le tissu sera généralement moins solide et assez élastique.', '2023-05-11 13:12:58', '2023-05-11 13:12:58', NULL),
(43, 'Poignet', 'poignet', 'Partie basse d\'une manche longue terminée par une manchette, un revers, etc...', '2023-05-11 13:12:58', '2023-05-11 13:12:58', NULL),
(44, 'Base', 'base', 'Tracé construit à partir de mesures d\'un corps (avec ou sans aisance), auquel on peut ensuite appliquer des transformations.', '2023-05-11 13:12:58', '2023-05-11 13:12:58', NULL),
(45, 'Laize', 'laize', 'La laize désigne tout simplement la largeur d’un tissu. Comme vous déterminez la longueur de l’étoffe à l’achat, la laize est préalablement fixée par le fabricant du tissu. Elle est généralement de 150 cm pour les tissus destinés à l’habillement, et un peu plus de 150 cm pour les tissus d’ameublement.', '2023-05-11 13:12:58', '2023-05-11 13:12:58', NULL),
(46, 'Modélisme', 'modelisme', '(de mode). Action de concevoir un volume de vêtement à partir d\'un dessin, soit par coupe à plat (en 2D), soit par moulage (en 3D).', '2023-05-11 13:12:58', '2023-05-11 13:12:58', NULL),
(47, 'Trame', 'trame', 'Le contre fil est le sens des fils perpendiculaires à la lisière (et donc à un angle de 90° par rapport au droit-fil). Coupé suivant le sens contre fil, le tissu sera généralement moins solide et assez élastique.', '2023-05-11 13:12:58', '2023-05-11 13:12:58', NULL),
(48, 'Surjeter', 'surjeter', 'Surfiler est une étape incontournable de la finition qui consiste à coudre les bords du tissu pour que celui-ci ne s’effiloche pas. Elle se fait aisément avec une machine surjeteuse, mais si vous n’en possédez pas, ce n’est pas grave, utilisez le point zigzag sur une machine à coudre normale.', '2023-05-11 13:12:58', '2023-05-11 13:12:58', NULL),
(49, 'Baleine', 'baleine', 'Tige de métal ou de plastique souple permettant de renforcer et de maintenir différentes parties d\'un vêtement, exemple les bustiers.', '2023-05-11 13:12:58', '2023-05-11 13:12:58', NULL),
(50, 'Tricot', 'tricot', 'Pièce de tissu fixée à la base d\'un col tailleur pour lui donner du maintien.', '2023-05-11 13:12:58', '2023-05-11 13:12:58', NULL),
(51, 'Banane', 'banane', 'L’endroit est la face du tissu qui est visible de l’extérieur, la face où les motifs sont imprimés et bien visibles. L’envers, c’est l’autre face, c’est-à-dire la face intérieure du tissu.', '2023-05-11 13:12:58', '2023-05-11 13:12:58', NULL),
(52, 'Équerrage', 'equerrage', 'Trait de construction effectué à l\'équerre par rapport à un autre trait (à 9o°), et cela en gênéral sur quelques centimètres, pour garantir, par exemple, le montage entre deux pièces', '2023-05-11 13:12:58', '2023-05-11 13:12:58', NULL),
(53, 'Faufiler', 'faufiler', 'Bâtir consiste à assembler les tissus provisoirement à la main, avant de piquer définitivement à la machine à coudre. Comme un brouillon, bâtir nous permet d’avoir un aperçu de l’ouvrage, mais cette pratique permet également d’éviter que les tissus bougent pendant la manipulation en machine. Pour bâtir, on utilise un fil dont la couleur est en contraste avec celle du tissu cousu pour pouvoir retirer facilement les fils. Il est aussi recommandé d’utiliser des points larges et espacés, d’où le nom faufiler. Celui-ci est indispensable pour réaliser un ourlet par exemple.', '2023-05-11 13:12:58', '2023-05-11 13:12:58', NULL),
(54, 'Ampleur', 'ampleur', 'Représente le volume donné, ou retiré, à un patron ou un vêtement.', '2023-05-11 13:12:58', '2023-05-11 13:12:58', NULL),
(55, 'Soufflet', 'soufflet', '(ou gousset) Pièce de tissu insérée sous le bras lors de la conception d\'un kimono. En forme de losange, d\'une façon clas-sique, cette pièce est généralement coupée dans le plein biais. INSERT INTO `gradations` (`id`, `name`, `slug`, `base`, `min`, `max`, `image`, `variation`, `created_at`, `updated_at`, `deleted_at`) VALUES
